import React from 'react';
import BidForm from './BidForm';
import CountdownTimer from './CountdownTimer';

const FurnitureItem = ({ furnitureItem, handleDelete, handleEdit, handleBid }) => {
  const formatDate = (isoDateString) => {
    const date = new Date(isoDateString);
    const options = {
      year: 'numeric',
      month: 'short',
      day: '2-digit',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true
    };
    return new Intl.DateTimeFormat('en-US', options).format(date);
  };

  return (
    <li className="furniture-item">
      <button 
        className="delete-button"
        onClick={() => handleDelete(furnitureItem.id)}
      >
        &times;
      </button>
      <button 
        className="edit-button"
        onClick={() => handleEdit(furnitureItem._ids)}
      >
        &#9998;
      </button>
      <img className="furniture-img" src={furnitureItem.imageUrl} alt={furnitureItem.name} />
      <h3>{furnitureItem.name}</h3>
      <p>{furnitureItem.description}</p>
      <p>Starting Bid: ${furnitureItem.startingBid}</p>
      <p>Current Bid: ${furnitureItem.currentBid}</p>
      <p>Auction End Date: {formatDate(furnitureItem.auctionEnd)}</p>
      <BidForm 
        furnitureItem={furnitureItem} 
        handleBid={handleBid} 
      />
       <CountdownTimer auctionEnd={furnitureItem.auctionEnd} />
    </li>
  );
};

export default FurnitureItem;
