import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [furnitureItems, setFurnitureItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:8000/api/furniture-items')
      .then(response => {
        console.log("Fetched items:", response.data); // Debugging log
        setFurnitureItems(response.data);
      })
      .catch(error => {
        console.error("There was an error fetching the furniture items!", error);
      });
  }, []);

  const handleDelete = (id) => {
    console.log("Deleting item with id:", id); // Debugging log
    axios.delete(`http://localhost:8000/api/furniture-items/${id}`)
      .then(response => {
        console.log(response.data); // Check the response from the server
        setFurnitureItems(furnitureItems.filter(item => item.id !== id));
      })
      .catch(error => {
        console.error("There was an error deleting the furniture item!", error);
      });
  };


  const handleEdit = (_id) => {
    navigate(`/edit/${_id}`);
  };

  const handleSearch = () => {
    if (searchTerm.trim() === '') {
      axios.get('http://localhost:8000/api/furniture-items')
        .then(response => {
          setFurnitureItems(response.data);
        })
        .catch(error => {
          console.error("There was an error fetching the furniture items!", error);
        });
    } else {
      const filteredItems = furnitureItems.filter(item =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFurnitureItems(filteredItems);
    }
  };

  const handleBid = (_id, newBid) => {
    console.log("Bidding on item with id:", _id); // Debugging log
    console.log("New bid:", newBid); // Debugging log
    console.log("Current furniture items:", furnitureItems); // Debugging log

    const item = furnitureItems.find(item => item._id === _id);
    if (!item) {
      console.error("Item not found!");
      return;
    }

    if (newBid > item.currentBid) {
      axios.put(`http://localhost:8000/api/furniture-items/bid/${_id}`, { currentBid: newBid })
        .then(response => {
          setFurnitureItems(furnitureItems.map(item => item._id === _id ? response.data : item));
        })
        .catch(error => {
          console.error("There was an error updating the bid!", error.response.data); // Debugging log
        });
    } else {
      alert("Bid amount must be higher than the current bid.");
    }
  };

  const handleInvalid = (e) => {
    if (e.target.validity.valueMissing) {
      e.target.setCustomValidity('Please enter a bid amount.');
    } else if (e.target.validity.rangeUnderflow) {
      e.target.setCustomValidity('Your bid must be higher than the current bid.');
    } else {
      e.target.setCustomValidity('');
    }
  };

  const formatDate = (isoDateString) => {
    const date = new Date(isoDateString);
    const options = {
      year: 'numeric',
      month: 'short',
      day: '2-digit',
      hour: 'numeric',
      minute: 'numeric',
      hour12: true // Use 12-hour time with AM/PM
    };
    return new Intl.DateTimeFormat('en-US', options).format(date);
  };

  return (
    <div className="main-content home">
      <h1>Place Your Bids!</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for items..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <ul className="furniture-items">
        {furnitureItems.map((furnitureItem) => (
          <li className="furniture-item" key={furnitureItem._id}>
            <button 
              className="delete-button"
              onClick={() => handleDelete(furnitureItem.id)}
            >
              &times;
            </button>
            <button 
              className="edit-button"
              onClick={() => handleEdit(furnitureItem._id)}
            >
              &#9998; {/* Pencil icon */}
            </button>
            <img className="furniture-img" src={furnitureItem.imageUrl} alt={furnitureItem.name} />
            <h3>{furnitureItem.name}</h3>
            <p>{furnitureItem.description}</p>
            <p>Starting Bid: ${furnitureItem.startingBid}</p>
            <p>Current Bid: ${furnitureItem.currentBid}</p>
            <p>Auction End Date: {formatDate(furnitureItem.auctionEnd)}</p>
            <form className="bid-form" onSubmit={(e) => {
              e.preventDefault();
              const newBid = parseFloat(e.target.elements.newBid.value);
              handleBid(furnitureItem._id, newBid);
            }}>
              <input 
                type="number" 
                name="newBid" 
                min={furnitureItem.currentBid + 1} 
                step="0.01" 
                required 
                onInvalid={handleInvalid} 
                onInput={(e) => e.target.setCustomValidity('')} // Clear the custom message when user starts typing
              />
              <button type="submit">Bid!</button>
            </form>
          </li>
        ))}    
      </ul>
    </div>
  );
}

export default Home;
