import React from 'react';

const About = (props) => (
  <div className="main-content">
    <h1>{props.title}</h1>
    <p className="about-us">
      Welcome to Furniture Auction, your premier destination for discovering and bidding on high-quality furniture pieces. Our platform connects buyers with a diverse range of furniture from various sellers, offering a unique and competitive shopping experience. At Furniture Auction, we believe that finding the perfect piece of furniture should be exciting and accessible to everyone. Whether you are looking for modern, vintage, or custom-made furniture, our extensive listings have something to suit every taste and budget. Our mission is to provide a seamless and enjoyable auction experience. We ensure all listings are thoroughly vetted and described in detail to help you make informed bidding decisions. With our secure and user-friendly platform, you can bid with confidence from the comfort of your home. Thank you for choosing Furniture Auction. We are dedicated to bringing you the best in furniture auctions and look forward to helping you find your next treasured piece.
    </p>
  </div>
);

export default About;
